var myBucket = gcs.bucket('my-bucket');
var remoteFile = myBucket.file('image.png');
var localFilename = '/Users/stephen/Photos/image.png';

remoteFile.createReadStream()
  .on('error', function(err) {})
  .on('response', function(response) {
