File.prototype.startResumableUpload_ = function(dup, metadata) {
  var self = this;

  var uploadStream = resumableUpload({
    authClient: this.bucket.storage.makeAuthorizedRequest_.authClient,
    bucket: this.bucket.name,
    file: this.name,
    generation: this.generation,
    metadata: metadata || {}
  });

  uploadStream
    .on('response', function(resp, metadata) {
      if (metadata) {
        self.metadata = metadata;
      }
    })
    .on('finish', function() {
      dup.emit('complete');
    });

  dup.setWritable(uploadStream);
};

File.prototype.startSimpleUpload_ = function(dup, metadata) {
  var self = this;

  var reqOpts = {
    qs: {
      name: self.name
    },
    uri: format('{base}/{bucket}/o', {
      base: STORAGE_UPLOAD_BASE_URL,
      bucket: self.bucket.name
    })
  };

  if (this.generation) {
    reqOpts.qs.ifGenerationMatch = this.generation;
  }

  util.makeWritableStream(dup, {
    makeAuthorizedRequest: self.bucket.storage.makeAuthorizedRequest_,
    metadata: metadata,
    request: reqOpts
  }, function(data) {
    self.metadata = data;
    dup.emit('complete');
  });
};

module.exports = File;

